import React, { useState } from 'react';
import { View, Text, Image, Pressable, StyleSheet, Dimensions } from 'react-native';
import { useNavigation } from '@react-navigation/native';

const { height: screenHeight } = Dimensions.get('window');

const MedicineLists = () => {
  const navigation = useNavigation();

  const [selectedCategory, setSelectedCategory] = useState(null);

  const categories = [
    'Allergy Care',
    'Body & Muscle Pain',
    "Children's Health",
    'Colds Medicines',
    'Cough Medicines',
    'Gut and Stomach Care',
    'Headache',
    'Fever & Flu',
    'Prescription Drugs',
  ];

  const allergyCareMedicines = [
    'Allerta® Tablet',
    'Alnix® Plus Tablet',
    'Alnix® Tablet',
    'AT',
  ];

  const handleCategoryClick = (index) => {
    setSelectedCategory(index === selectedCategory ? null : index);
  };

  const handleMedicineClick = (medicine) => {
    // Navigate to the MedicineDetails page with the selected medicine
    navigation.navigate('MedicineDetails', { medicine });
  };

  return (
    <View style={styles.container}>
      <View style={styles.upperHalf}>
        <View style={styles.logoBackground}>
          <Image source={require('../assets/medicine.png')} style={styles.logo} />
        </View>
        <Text style={styles.title}>Allergy Care</Text>
      </View>
      <View style={styles.lowerHalf}>
        {selectedCategory === 0 && (
          <View>
            {allergyCareMedicines.map((medicine, index) => (
              <Pressable
                key={index}
                onPress={() => handleMedicineClick(medicine)}
                style={styles.barContainer}
              >
                <Text style={styles.barTitle}>{medicine}</Text>
              </Pressable>
            ))}
          </View>
        )}
        <Text style={styles.title}>Medicine List</Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  upperHalf: {
    height: screenHeight * 0.3,
    backgroundColor: '#F2ABFF',
    alignItems: 'center',
    justifyContent: 'flex-end',
    borderBottomLeftRadius: 30,
    borderBottomRightRadius: 30,
  },
  logoBackground: {
    width: 100,
    height: 100,
    backgroundColor: 'white',
    borderRadius: 50,
    alignItems: 'center',
    justifyContent: 'center',
  },
  logo: {
    width: 80,
    height: 80,
    resizeMode: 'cover',
  },
  lowerHalf: {
    backgroundColor: '#FADEFF',
    flex: 1,
    paddingHorizontal: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
    color: '#A3007F',
  },
  barContainer: {
    marginBottom: 10,
    borderColor: '#ccc',
    padding: 6,
    borderRadius: 20,
    backgroundColor: 'white',
    fontStyle: 'italic',
  },
  barTitle: {
    fontSize: 16,
    fontWeight: 'italic',
    marginBottom: 5,
  },
});

export default MedicineLists;
