import React, { useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, ScrollView } from 'react-native';

const MedicineDetails = () => {
  const [selectedCategory, setSelectedCategory] = useState(null);

  const categories = [
    {
      title: 'Description',
      info: 'This is the description of the medicine.',
    },
    {
      title: 'Dosage and Frequency',
      info: 'Dosage and frequency information for the medicine.',
    },
    {
      title: 'Precautions',
      info: 'Precautionary measures for taking the medicine.',
    },
    {
      title: 'Overdosage of the medicine.',
      info: 'Information about overdosage of the medicine.',
    },
  ];

  const handleCategoryClick = (index) => {
    setSelectedCategory(index === selectedCategory ? null : index);
  };

  return (
    <ScrollView contentContainerStyle={styles.container}>
      {categories.map((category, index) => (
        <TouchableOpacity
          key={index}
          onPress={() => handleCategoryClick(index)}
          style={styles.barContainer}
        >
          <View style={styles.barTitleContainer}>
            <Text style={styles.barTitle}>{category.title}</Text>
            <Text style={styles.downArrow}>â–¼</Text>
          </View>
          {selectedCategory === index && (
            <View style={styles.infoContainer}>
              <Text style={styles.infoText}>{category.info}</Text>
            </View>
          )}
        </TouchableOpacity>
      ))}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    paddingTop: 100,
    paddingHorizontal: 20,
    backgroundColor: '#ffffff',
  },
  barContainer: {
    marginBottom: 20,
    borderWidth: 1,
    borderColor: '#ccc',
    padding: 10,
    borderRadius: 10,
    width: '100%',
  },
  barTitleContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  barTitle: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  downArrow: {
    fontSize: 16,
  },
  infoContainer: {
    marginTop: 10,
    backgroundColor: '#ADD8E6', 
    padding: 10,
    borderRadius: 10,
  },
  infoText: {
    fontSize: 14,
  },
});


export default MedicineDetails;
