import React, { useState } from "react";
import {
  View,
  Text,
  TextInput,
  Pressable,
  StyleSheet,
  Platform,
} from "react-native";
import { useNavigation } from "@react-navigation/native";
import ApiService from "../services/ApiService";
import DatePicker from "react-native-datepicker";

const SignUpScreen = () => {
  const navigation = useNavigation();
  const [userData, setUserData] = useState({
    fullName: '',
    dateOfBirth: new Date(),
    contactNumber: '',
    email: '',
    password: '',
  });

  const handleSignUpPress = async () => {
    try {
      const response = await ApiService.registerUser(userData);
      navigation.navigate('Login');
    } catch (error) {
      console.error('Error during registration:', error.message);
      // Handle registration error
    }
  };

  const handleDateChange = (date) => {
    setUserData({
      ...userData,
      dateOfBirth: date,
    });
  };

  return (
    <View style={styles.container}>
      <TextInput
        placeholder="Full Name"
        style={styles.input}
        value={userData.fullName}
        onChangeText={(text) => setUserData({ ...userData, fullName: text })}
      />

      {/* Render different date pickers based on the platform */}
      {Platform.OS === 'web' ? (
        <DatePicker
          selected={userData.dateOfBirth}
          onChange={handleDateChange}
        />
      ) : (
        <DatePicker
          style={styles.datePicker}
          date={userData.dateOfBirth}
          mode="date"
          format="MM/dd/yyyy"
          confirmBtnText="Confirm"
          cancelBtnText="Cancel"
          customStyles={{
            dateInput: styles.dateInput,
            // Add other custom styles if needed
          }}
          onDateChange={(date) => handleDateChange(date)}
        />
      )}

      <TextInput
        placeholder="Contact Number"
        style={styles.input}
        value={userData.contactNumber}
        onChangeText={(text) => setUserData({ ...userData, contactNumber: text })}
      />
      <TextInput
        placeholder="Email"
        style={styles.input}
        value={userData.email}
        onChangeText={(text) => setUserData({ ...userData, email: text })}
      />
      <TextInput
        placeholder="Password"
        style={styles.input}
        secureTextEntry={true}
        value={userData.password}
        onChangeText={(text) => setUserData({ ...userData, password: text })}
      />

      <Pressable style={styles.signUpButton} onPress={handleSignUpPress}>
        <Text style={styles.buttonText}>Sign Up</Text>
      </Pressable>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "skyblue",
    padding: 20,
    justifyContent: "center",
    borderRadius: 10,
    shadowColor: "black",
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.5,
    shadowRadius: 3,
    elevation: 5,
  },
  header: {
    alignItems: "center",
    marginTop: -60,
  },
  welcomeText: {
    fontSize: 24,
    fontWeight: "bold",
    position: "absolute",
  },
  form: {
    marginTop: 40,
  },
  input: {
    backgroundColor: "white",
    borderRadius: 10,
    padding: 10,
    marginBottom: 15,
    shadowColor: "black",
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 2,
    elevation: 3,
  },
  signUpButton: {
    backgroundColor: "lightblue",
    borderRadius: 10,
    padding: 15,
    alignItems: "center",
    shadowColor: "black",
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 2,
    elevation: 3,
  },
  buttonText: {
    color: "white",
    fontWeight: "bold",
  },
  dateText: {
    // Style for the DatePicker container
    backgroundColor: "white",
    borderRadius: 10,
    padding: 10,
    marginBottom: 15,
    shadowColor: "black",
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 2,
    elevation: 3,
  },
  dateInput: {
    // Style for the DatePicker input field
    borderWidth: 0,
  },
  dateInputText: {
    // Style for the DatePicker date text
    fontSize: 16,
  },
  datePlaceholderText: {
    // Style for the DatePicker placeholder text
    fontSize: 16,
  },
});

export default SignUpScreen;
