import React, { useState } from 'react';
import { View, Text, Image, TextInput, TouchableOpacity, StyleSheet, Dimensions, Alert } from 'react-native';
import { useNavigation, useRoute } from '@react-navigation/native';
import { Calendar } from 'react-native-calendars';

const { height: screenHeight } = Dimensions.get('window');

const HomePage = () => {
  const navigation = useNavigation();
  const route = useRoute();

  const fullName = route.params?.fullName;

  // Initialize searchText as an empty string
  const [searchText, setSearchText] = useState('');
  const [selectedDates, setSelectedDates] = useState({});

  const handleDateSelect = (day) => {
    const selectedDate = day.dateString;
    setSelectedDates((prevSelectedDates) => ({
      ...prevSelectedDates,
      [selectedDate]: {
        selected: !prevSelectedDates[selectedDate]?.selected,
      },
    }));

    // Display a basic alert when a date is selected
    Alert.alert(
      'Calendar Event',
      `You have an event on ${selectedDate}`,
      [
        {
          text: 'OK',
          onPress: () => console.log('OK Pressed'),
        },
      ],
      { cancelable: false }
    );
  };

  const handleSearch = () => {
    const searchLowerCase = searchText.toLowerCase();
    const isVitaminSearch = searchLowerCase.includes('vitamin');

    if (isVitaminSearch) {
      navigation.navigate('Vitamins');
    } else if (searchText.trim() !== '') {
      navigation.navigate('MedicineDetails', { medicine: searchText });
    }
  };

  const profileInfo = {
    username: 'JohnDoe',
    email: 'johndoe@example.com',// Sample avatar image
  };

  return (
    <View style={styles.container}>
      <View style={styles.sidebar}>
        <View style={styles.profile}>
          <Text style={styles.username}>{profileInfo.username}</Text>
          <Text style={styles.email}>{profileInfo.email}</Text>
        </View>
        {/* Add more profile-related information if needed */}
      </View>
      <View style={styles.mainContent}>
        {/* Your existing content (upperHalf, lowerHalf) goes here */}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'row', // Use row layout to display sidebar and content side by side
    backgroundColor: '#C9F5FF',
  },
  sidebar: {
    flex: 1, // Occupy 1/4th of the screen for the sidebar
    backgroundColor: '#6499E9',
    padding: 20,
    justifyContent: 'center',
    alignItems: 'center',
  },
  profile: {
    alignItems: 'center',
    marginBottom: 20,
  },
  avatar: {
    width: 80,
    height: 80,
    borderRadius: 40,
    marginBottom: 10,
  },
  username: {
    fontSize: 18,
    fontWeight: 'bold',
    color: 'white',
  },
  email: {
    fontSize: 14,
    color: 'white',
  },
  mainContent: {
    flex: 3, // Occupy 3/4th of the screen for the main content
    paddingHorizontal: 20,
  },
  container: {
    flex: 1,
    backgroundColor: '#C9F5FF',
  },
  upperHalf: {
    height: screenHeight * 0.4,
    flexDirection: 'column',
    alignItems: 'center',
    backgroundColor: '#6499E9',
    justifyContent: 'center',
    borderBottomRightRadius: 40,
    borderBottomLeftRadius: 40,
  },
  lowerHalf: {
    flex: 1,
    paddingHorizontal: 20,
  },
  smallText: {
    fontSize: 12,
    marginBottom: 10,
    position: 'relative',
    marginLeft: -200,
    color: 'white',
  },
  bigText: {
    fontSize: 40,
    marginBottom: 10,
    position: 'relative',
    color: 'white',
  },
  searchContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: 'white',
    borderRadius: 25,
    paddingHorizontal: 20, // Adjusted padding
    marginHorizontal: 10, // Adjusted margin
    marginTop: 10, // Adjusted margin
  },
  searchInput: {
    flex: 1, // Adjusted flex
    fontSize: 16,
    paddingVertical: 5,
  },
  searchIcon: {
    padding: 10,
    marginLeft: 'auto', // Align to the right end
  },
  searchImage: {
    width: 20,
    height: 20,
  },
  calendarContainer: {
    marginTop: 20,
    aspectRatio: 1,
    overflow: 'scroll',
    borderRadius: 20,
    borderBottomLeftRadius: 40,
    borderBottomRightRadius: 40,
  },
  categoriesText: {
    fontSize: 18,
    fontWeight: 'bold',
  },
  iconContainer: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    marginTop: 20,
  },
  circleBackground: {
    backgroundColor: 'white',
    borderRadius: 50,
    padding: 5,
  },
  icon: {
    alignItems: 'center',
  },
  iconImage: {
    width: 60,
    height: 60,
    resizeMode: 'contain',
  },
});

export default HomePage;
