import React, { useState } from 'react';
import { View, Text, Image, Pressable, StyleSheet, Dimensions } from 'react-native';

const { height: screenHeight } = Dimensions.get('window');

const MedicineLists = () => {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedMedicine, setSelectedMedicine] = useState(null);

  const categories = [
    'Allergy Care',
    'Body & Muscle Pain',
    "Children's Health",
    'Colds Medicines',
    'Cough Medicines',
    'Gut and Stomach Care',
    'Headache',
    'Fever & Flu',
    'Prescription Drugs',
  ];

  const allergyMedications = [
    'Allerta® Tablet',
    'Alnix® Plus Tablet',
    'Alnix® Tablet',
  ];

  const handleCategoryClick = (index) => {
    setSelectedCategory(index === selectedCategory ? null : index);
  };

  const handleMedicineClick = (medicine) => {
    setSelectedMedicine(medicine);
    // Navigate to the medicine details page using navigation or show details in a modal
    // Example: navigation.navigate('MedicineDetails', { medicine });
  };

  return (
    <View style={styles.container}>
      <View style={styles.upperHalf}>
        <View style={styles.logoBackground}>
          <Image source={require('../assets/medicine.png')} style={styles.logo} />
        </View>
        <Text style={styles.title}>Medicine List</Text> {/* Moved the title under the logo */}
      </View>
      <View style={styles.lowerHalf}>
        {categories.map((category, index) => (
          <Pressable
            key={index}
            onPress={() => handleCategoryClick(index)}
            style={styles.barContainer}
          >
            <View style={styles.barTitleContainer}>
              <Text style={styles.barTitle}>{category}</Text>
              <Text style={styles.downArrow}>
                {selectedCategory === index ? '▲' : '▼'}
              </Text>
            </View>
            {selectedCategory === index && category === 'Allergy Care' && (
              <View style={styles.medicineList}>
                {allergyMedications.map((medicine, medIndex) => (
                  <Pressable
                    key={medIndex}
                    onPress={() => handleMedicineClick(medicine)}
                    style={styles.medicineItem}
                  >
                    <Text style={styles.medicineText}>{medicine}</Text>
                  </Pressable>
                ))}
              </View>
            )}
          </Pressable>
        ))}
        {selectedMedicine && (
          <Text style={styles.infoText}>
            This is the information for {selectedMedicine}.
          </Text>
        )}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  upperHalf: {
    height: screenHeight * 0.3,
    justifyContent: 'center',
    backgroundColor: '#F2ABFF',
    alignItems: 'center',
    borderBottomLeftRadius: 40,
    borderBottomRightRadius: 40,
  },
  logoBackground: {
    width: 100,
    height: 100,
    backgroundColor: 'white',
    borderRadius: 50,
    alignItems: 'center',
    justifyContent: 'center',
  },
  logo: {
    width: 80,
    height: 80,
    resizeMode: 'cover',
  },
  lowerHalf: {
    backgroundColor: '#FADEFF',
    flex: 1,
    paddingHorizontal: 20,
    marginTop: 10,
  },
  title: {
    fontSize: 30,
    fontWeight: 'bold',
    marginBottom: 10,
    color: '#A3007F',
  },
  barContainer: {
    marginBottom: 10,
    borderColor: '#ccc',
    padding: 6,
    borderRadius: 20,
    backgroundColor: 'white',
    fontStyle: 'italic',
  },
  barTitleContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'baseline',
  },
  barTitle: {
    fontSize: 16,
    fontWeight: 'italic',
    marginBottom: 5,
  },
  downArrow: {
    fontSize: 12,
  },
  medicineList: {
    marginVertical: 10,
  },
  medicineItem: {
    borderColor: '#ccc',
    padding: 6,
    borderRadius: 20,
    backgroundColor: 'white',
    marginVertical: 5,
  },
  medicineText: {
    fontSize: 16,
  },
  infoText: {
    fontSize: 14,
  },
});

export default MedicineLists;
