import React, { useState } from 'react';
import { View, Text, Image, TextInput, Pressable, StyleSheet, Dimensions } from 'react-native';
import { useNavigation, useRoute } from '@react-navigation/native';
import { Agenda } from 'react-native-calendars'; // Import the Agenda component

const { height: screenHeight } = Dimensions.get('window');

const HomePage = () => {
  const navigation = useNavigation();
  const route = useRoute();

  // Extract the fullName parameter from the route's params
  const fullName = route.params?.fullName;

  const [searchText, setSearchText] = useState('');
  const [calendarEvents, setCalendarEvents] = useState({});

  const handleSearch = () => {
    const searchLowerCase = searchText.toLowerCase();

    if (searchLowerCase.includes('vitamin')) {
      navigation.navigate('Vitamins');
    } else {
      navigation.navigate('MedicineDetails', { searchText });
    }
  };

  const addCalendarEvent = (date, event) => {
    setCalendarEvents((prevEvents) => ({
      ...prevEvents,
      [date]: [{ name: event, timestamp: new Date().getTime() }, ...prevEvents[date]],
    }));
  };

  return (
    <View style={styles.container}>
      <View style={styles.upperHalf}>
        <Text style={styles.smallText}>Welcome {fullName}</Text>
        <Text style={styles.bigText}>Finding a perfect medicine?</Text>
        <View style={styles.searchContainer}>
          <TextInput
            style={styles.searchInput}
            placeholder="Search medicine ex.'Biogesic'"
            onChangeText={setSearchText}
            value={searchText}
          />
          <Pressable onPress={handleSearch} style={styles.searchIcon}>
            <Image source={require('../assets/search.png')} style={styles.searchImage} />
          </Pressable>
        </View>
        <Pressable
          style={styles.calendarButton}
          onPress={() => {
            navigation.navigate('CalendarScreen', { addCalendarEvent });
          }}
        >
          <Text style={styles.calendarText}>Calendar</Text>
        </Pressable>
      </View>
      <View style={styles.lowerHalf}>
        <Text style={styles.categoriesText}>Categories</Text>
        {/* Your category icons and navigation code here */}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#00F1C6',
  },
  upperHalf: {
    height: screenHeight * 0.4,
    flexDirection: 'column',
    alignItems: 'center',
    backgroundColor: '#6499E9',
    justifyContent: 'center',
    borderBottomRightRadius: 40,
    borderBottomLeftRadius: 40
  },
  lowerHalf: {
    marginTop: 10,
    flex: 1,
    paddingHorizontal: 20,
  },
  smallText: {
    fontSize: 12,
    marginBottom: 10,
    position: 'relative',
    marginLeft: -200,
    color: 'white'
  },
  bigText: {
    fontSize: 40,
    marginBottom: 10,
    position: 'relative',
    color: 'white'
  },
  searchContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: 'white',
    borderRadius: 25,
    paddingLeft: 4,
  },
  searchInput: {
    flex: 10,
    fontSize: 16,
    paddingVertical: 5,
  },
  searchIcon: {
    padding: 10,
  },
  searchImage: {
    width: 20,
    height: 20,
  },
  categoriesText: {
    fontSize: 18,
    fontWeight: 'bold',
  },
  iconContainer: {
    flexDirection: 'row',
    justifyContent: 'center',
    marginTop: 10,
  },
  icon: {
    alignItems: 'center',
    marginHorizontal: 10,
  },
  iconImage: {
    width: 60,
    height: 60,
    resizeMode: 'contain',
  },
  iconText: {
    marginTop: 5,
    fontSize: 14,
  },
  calendarButton: {
    backgroundColor: 'white',
    paddingHorizontal: 20,
    paddingVertical: 10,
    borderRadius: 25,
    marginTop: 20,
  },
  calendarText: {
    fontSize: 16,
  },
});

export default HomePage;
