import React, { useState } from 'react';
import { View, Picker, Text, StyleSheet } from 'react-native';

const MedicineDescription = () => {
  const [selectedValue, setSelectedValue] = useState('');
  const [description, setDescription] = useState('');

  const medicineDescriptions = {
    Description: 'gamot'
  };

  const handleValueChange = (itemValue) => {
    setSelectedValue(itemValue);
    setDescription(medicineDescriptions[itemValue] || '');
  };

  return (
    <View style={styles.container}>
      <View style={styles.titleContainer}>
        <Text style={styles.title}>Medicine Description</Text>
      </View>
      <Picker
        selectedValue={selectedValue}
        style={styles.picker}
        onValueChange={(itemValue) => handleValueChange(itemValue)}
      >
        <Picker.Item label="Select Medicine" value="" />
        <Picker.Item label="Java" value="java" />
        <Picker.Item label="JavaScript" value="js" />
        <Picker.Item label="Python" value="python" />
        <Picker.Item label="C#" value="csharp" />
      </Picker>
      {description ? (
        <View style={styles.descriptionContainer}>
          <Text style={styles.descriptionText}>Description: {description}</Text>
        </View>
      ) : null}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    padding: 20,
  },
  titleContainer: {
    marginBottom: 10,
  },
  title: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  picker: {
    height: 40,
    width: 200,
  },
  descriptionContainer: {
    marginTop: 10,
  },
  descriptionText: {
    fontSize: 16,
  },
});

export default MedicineDescription;
