import React, { useState } from "react";
import {
  View,
  Text,
  TextInput,
  Pressable,
  StyleSheet,
  Platform,
  DatePickerIOS,
} from "react-native";
import { useNavigation } from "@react-navigation/native";
import ApiService from "../services/ApiService"; // Import ApiService
import DateTimePicker from "@react-native-community/datetimepicker"; // Import DateTimePicker

const SignUpScreen = () => {
  const navigation = useNavigation();
  const [userData, setUserData] = useState({
    fullName: "",
    dateOfBirth: new Date(), // Initialize dateOfBirth with today's date
    contactNumber: "",
    email: "",
    password: "",
    showDatePicker: Platform.OS === "ios", // Platform-specific flag to show/hide DatePicker
  });

  const handleSignUpPress = async () => {
    try {
      const response = await ApiService.registerUser(userData);
      navigation.navigate("Login");
    } catch (error) {
      console.error("Error during registration:", error.message);
      // Handle registration error
    }
  };

  const handleDateChange = (event, selectedDate) => {
    setUserData({
      ...userData,
      dateOfBirth: selectedDate,
      showDatePicker: Platform.OS === "ios",
    });
  };

  const showDatePicker = () => {
    setUserData({ ...userData, showDatePicker: true });
  };

  const hideDatePicker = () => {
    setUserData({ ...userData, showDatePicker: false });
  };

  return (
    <View style={styles.container}>
      <TextInput
        placeholder="Full Name"
        style={styles.input}
        value={userData.fullName}
        onChangeText={(text) => handleInputChange('fullName', text)}
      />
      <Pressable onPress={showDatePicker}>
        <Text style={styles.dateText}>
          {userData.dateOfBirth.toLocaleDateString()}{" "}
          {/* Display formatted date */}
        </Text>
      </Pressable>

      {userData.showDatePicker && Platform.OS === "ios" ? (
        <DateTimePicker
          value={userData.dateOfBirth}
          mode="date"
          display="spinner"
          onChange={handleDateChange}
        />
      ) : null}
      <TextInput
        placeholder="Contact Number"
        style={styles.input}
        value={userData.contactNumber}
        onChangeText={(text) => handleInputChange('contactNumber', text)}
      />
      <TextInput
        placeholder="Email"
        style={styles.input}
        value={userData.email}
        onChangeText={(text) => handleInputChange('email', text)}
      />
      <TextInput
        placeholder="Password"
        style={styles.input}
        secureTextEntry={true}
        value={userData.password}
        onChangeText={(text) => handleInputChange('password', text)}
      />

      <Pressable style={styles.signUpButton} onPress={handleSignUpPress}>
        <Text style={styles.buttonText}>Sign Up</Text>
      </Pressable>
    </View>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "skyblue",
    padding: 20,
    justifyContent: "center",
    borderRadius: 10,
    shadowColor: "black",
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.5,
    shadowRadius: 3,
    elevation: 5,
  },
  header: {
    alignItems: "center",
    marginTop: -60,
  },
  welcomeText: {
    fontSize: 24,
    fontWeight: "bold",
    position: "absolute",
  },
  form: {
    marginTop: 40,
  },
  input: {
    backgroundColor: "white",
    borderRadius: 10,
    padding: 10,
    marginBottom: 15,
    shadowColor: "black",
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 2,
    elevation: 3,
  },
  signUpButton: {
    backgroundColor: "lightblue",
    borderRadius: 10,
    padding: 15,
    alignItems: "center",
    shadowColor: "black",
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 2,
    elevation: 3,
  },
  buttonText: {
    color: "white",
    fontWeight: "bold",
  },
  dateText: {
    backgroundColor: "white",
    borderRadius: 10,
    padding: 10,
    marginBottom: 15,
    shadowColor: "black",
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 2,
    elevation: 3,
  },
});

export default SignUpScreen;
