import React, { useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Image, Dimensions } from 'react-native';

const { height: screenHeight } = Dimensions.get('window');

const MedicineDetails = () => {
  const [selectedBubble, setSelectedBubble] = useState(null);

  const bubbleData = [
    { title: 'A', description: 'Description for A', image: require('path to image A') },
    // Add more bubble data for B, C, D, E, K, B1, B2, B3, B6, B7, B9
  ];

  const handleBubbleClick = (index) => {
    setSelectedBubble(index === selectedBubble ? null : index);
  };

  return (
    <View style={styles.container}>
      <View style={styles.upperHalf}>
        {/* Upper half content */}
        <Image source={require('C:/Users/makir/OneDrive/Desktop/Makiramdam_026_41S6/Project/Project/assets/human2.png')} style={styles.vitaminsImage} />
        <Text style={styles.title}>Vitamins Cheat Sheet</Text>
        <TouchableOpacity style={styles.backIconContainer}>
          <View style={styles.backIconCircle}>
            <Image source={require('C:/Users/makir/OneDrive/Desktop/Makiramdam_026_41S6/Project/Project/assets/backicon.png')} style={styles.backIcon} />
          </View>
        </TouchableOpacity>
      </View>
      <View style={styles.lowerHalf}>
        {/* Lower half content */}
        {bubbleData.map((bubble, index) => (
          <TouchableOpacity
            key={index}
            onPress={() => handleBubbleClick(index)}
            style={[styles.bubbleContainer, { backgroundColor: selectedBubble === index ? '#89fff8' : 'white' }]}
          >
            <Text style={styles.bubbleTitle}>{bubble.title}</Text>
            {selectedBubble === index && (
              <View style={styles.bubbleContent}>
                <Image source={require('C:/Users/makir/OneDrive/Desktop/Makiramdam_026_41S6/Project/Project/assets/human2.png')} style={styles.human2Image} />
                <Text style={styles.bubbleDescription}>{bubble.description}</Text>
              </View>
            )}
          </TouchableOpacity>
        ))}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#00F1C6',
  },
  upperHalf: {
    height: screenHeight * 0.4,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#89fff8',
    borderBottomLeftRadius: 40,
    borderBottomRightRadius: 40,
    position: 'relative',
  },
  vitaminsImage: {
    width: 80,
    height: 80,
    resizeMode: 'cover',
  },
  title: {
    marginTop: 10,
    fontSize: 24,
    fontWeight: 'bold',
    color: 'white',
  },
  backIconContainer: {
    position: 'absolute',
    top: 20,
    left: 20,
  },
  backIconCircle: {
    width: 40,
    height: 40,
    borderRadius: 20,
    backgroundColor: 'white',
    justifyContent: 'center',
    alignItems: 'center',
  },
  backIcon: {
    width: 20,
    height: 20,
  },
  lowerHalf: {
    marginTop: 10,
    flex: 1,
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'center',
    alignItems: 'center',
  },
  bubbleContainer: {
    width: '30%',
    aspectRatio: 1,
    borderRadius: 100,
    margin: '3%',
    justifyContent: 'center',
    alignItems: 'center',
  },
  bubbleTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    color: 'black',
  },
  bubbleContent: {
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: 10,
  },
  human2Image: {
    width: 50,
    height: 50,
    marginRight: 10,
  },
  bubbleDescription: {
    fontSize: 14,
  },
});

export default MedicineDetails;
