// ForgotPasswordScreen.js
import React from 'react';
import { View, Text, TextInput, Pressable, StyleSheet } from 'react-native';
import { useNavigation } from '@react-navigation/native';

const ForgotPassword = () => {
  const navigation = useNavigation();

  const handleBackToLogin = () => {
    navigation.navigate('Login');
  };

  const handleResetPassword = () => {
    // Add logic to handle password reset
    // This can include sending a reset link to the user's email, for example.
    // You can implement this functionality using your backend services.
    // For simplicity, let's just navigate back to the login screen.
    navigation.navigate('Login');
  };

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Forgot Password</Text>
      </View>
      <View style={styles.form}>
        <Text style={styles.subtitle}>
          Enter your email address to receive a password reset link.
        </Text>
        <TextInput
          placeholder="Email"
          style={styles.input}
          // Add onChangeText to handle email input
        />
        <Pressable style={styles.resetButton} onPress={handleResetPassword}>
          <Text style={styles.buttonText}>Reset Password</Text>
        </Pressable>
        <Pressable style={styles.backToLogin} onPress={handleBackToLogin}>
          <Text style={styles.backToLoginText}>Back to Login</Text>
        </Pressable>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'skyblue',
    padding: 20,
    justifyContent: 'center',
    borderRadius: 10,
    shadowColor: 'black',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.5,
    shadowRadius: 3,
    elevation: 5,
  },
  header: {
    alignItems: 'center',
    marginBottom: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: 'white',
  },
  form: {},
  subtitle: {
    fontSize: 16,
    color: 'white',
    marginBottom: 20,
  },
  input: {
    backgroundColor: 'white',
    borderRadius: 10,
    padding: 10,
    marginBottom: 15,
  },
  resetButton: {
    backgroundColor: 'lightblue',
    borderRadius: 10,
    padding: 15,
    alignItems: 'center',
  },
  buttonText: {
    color: 'white',
    fontWeight: 'bold',
  },
  backToLogin: {
    alignItems: 'center',
    marginTop: 10,
  },
  backToLoginText: {
    fontSize: 16,
    color: 'white',
    textDecorationLine: 'underline',
  },
});

export default ForgotPassword;
