import React, { useState } from 'react';
import { View, Text, Image, Pressable, StyleSheet, Dimensions } from 'react-native';

const { height: screenHeight } = Dimensions.get('window');

const MedicineLists = () => {
  const [selectedCategory, setSelectedCategory] = useState(null);

  const categories = [
    'Allergy Care',
    'Body & Muscle Pain',
    "Children's Health",
    'Colds Medicines',
    'Cough Medicines',
    'Gut and Stomach Care',
    'Headache',
    'Fever & Flu',
    'Prescription Drugs',
  ];

  const handleCategoryClick = (index) => {
    setSelectedCategory(index === selectedCategory ? null : index);
  };

  return (
    <View style={styles.container}>
      <View style={styles.upperHalf}>
        <Image source={require('../assets/logo.png')} style={styles.logo} />
      </View>
      <View style={styles.lowerHalf}>
        <Text style={styles.title}>Medicine Lists</Text>
        {categories.map((category, index) => (
          <Pressable
            key={index}
            onPress={() => handleCategoryClick(index)}
            style={styles.barContainer}
          >
            <View style={styles.barTitleContainer}>
              <Text style={styles.barTitle}>{category}</Text>
              <Text style={styles.downArrow}>
                {selectedCategory === index ? '▲' : '▼'}
              </Text>
            </View>
            {selectedCategory === index && (
              <View style={styles.infoContainer}>
                <Text style={styles.infoText}>
                  This is the information for {category}.
                </Text>
              </View>
            )}
          </Pressable>
        ))}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  upperHalf: {
    height: screenHeight * 0.3,
    backgroundColor: '#A3007F',
    alignItems: 'center',
    justifyContent: 'center',
  },
  logo: {
    width: 120,
    height: 120,
    resizeMode: 'cover',
  },
  lowerHalf: {
    backgroundColor: '#F2ABFF',
    flex: 1,
    paddingHorizontal: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginTop: 20,
  },
  barContainer: {
    marginBottom: 10,
    borderColor: '#ccc',
    padding: 6,
    borderRadius: 20,
    backgroundColor: 'white',
    fontStyle: 'italic',
  },
  barTitleContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'baseline',
  },
  barTitle: {
    fontSize: 16,
    fontWeight: 'italic',
    marginBottom: 5,
  },
  downArrow: {
    fontSize: 12,
  },
  infoContainer: {
    marginTop: 10,
  },
  infoText: {
    fontSize: 14,
  },
});

export default MedicineLists;
