import React, { useState } from 'react';
import { View, Picker, Text, StyleSheet } from 'react-native';

const MedicineDetails = () => {
  const [description, setDescription] = useState('');
  const [dosage, setDosage] = useState('');
  const [precautions, setPrecautions] = useState('');
  const [overdosage, setOverdosage] = useState('');

  const handleDescriptionChange = (itemValue) => {
    setDescription(itemValue);
  };

  const handleDosageChange = (itemValue) => {
    setDosage(itemValue);
  };

  const handlePrecautionsChange = (itemValue) => {
    setPrecautions(itemValue);
  };

  const handleOverdosageChange = (itemValue) => {
    setOverdosage(itemValue);
  };

  return (
    <View style={styles.container}>
      <View style={styles.barContainer}>
        <Text style={styles.barTitle}>Description</Text>
        <Picker
          selectedValue={description}
          style={styles.picker}
          onValueChange={(itemValue) => handleDescriptionChange(itemValue)}
        >
          <Picker.Item label="Select Description" value="" />
          <Picker.Item label="Description 1" value="desc1" />
          <Picker.Item label="Description 2" value="desc2" />
          <Picker.Item label="Description 3" value="desc3" />
        </Picker>
      </View>

      <View style={styles.barContainer}>
        <Text style={styles.barTitle}>Dosage and Frequency</Text>
        <Picker
          selectedValue={dosage}
          style={styles.picker}
          onValueChange={(itemValue) => handleDosageChange(itemValue)}
        >
          <Picker.Item label="Select Dosage and Frequency" value="" />
          <Picker.Item label="Dosage 1" value="dosage1" />
          <Picker.Item label="Dosage 2" value="dosage2" />
          <Picker.Item label="Dosage 3" value="dosage3" />
        </Picker>
      </View>

      <View style={styles.barContainer}>
        <Text style={styles.barTitle}>Precautions</Text>
        <Picker
          selectedValue={precautions}
          style={styles.picker}
          onValueChange={(itemValue) => handlePrecautionsChange(itemValue)}
        >
          <Picker.Item label="Select Precautions" value="" />
          <Picker.Item label="Precaution 1" value="precaution1" />
          <Picker.Item label="Precaution 2" value="precaution2" />
          <Picker.Item label="Precaution 3" value="precaution3" />
        </Picker>
      </View>

      <View style={styles.barContainer}>
        <Text style={styles.barTitle}>Overdosage</Text>
        <Picker
          selectedValue={overdosage}
          style={styles.picker}
          onValueChange={(itemValue) => handleOverdosageChange(itemValue)}
        >
          <Picker.Item label="Select Overdosage" value="" />
          <Picker.Item label="Overdosage 1" value="overdosage1" />
          <Picker.Item label="Overdosage 2" value="overdosage2" />
          <Picker.Item label="Overdosage 3" value="overdosage3" />
        </Picker>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
    backgroundColor: '#ffffff',
  },
  barContainer: {
    marginBottom: 20,
  },
  barTitle: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  picker: {
    height: 40,
    width: '100%',
  },
});

export default MedicineDetails;
