import React, { useState } from 'react';
import { View, Text, Image, TextInput, TouchableOpacity, StyleSheet, Dimensions } from 'react-native';
import { useNavigation } from '@react-navigation/native';

const { height: screenHeight } = Dimensions.get('window');

const HomePage = () => {
  const navigation = useNavigation();

  const [searchText, setSearchText] = useState('');

  const handleSearch = () => {
    if (searchText.trim() !== '') {
      navigation.navigate('MedicineDetails', { medicine: searchText });
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.upperHalf}>
        <Text style={styles.bigText}>Finding a perfect medicine?</Text>
        <View style={styles.searchContainer}>
          <TextInput
            style={styles.searchInput}
            placeholder="Search medicine ex. 'Biogesic'"
            onChangeText={setSearchText}
            value={searchText}
          />
          <TouchableOpacity onPress={handleSearch} style={styles.searchIcon}>
            <Image source={require('../assets/search.png')} style={styles.searchImage} />
          </TouchableOpacity>
        </View>
      </View>
      <View style={styles.lowerHalf}>
        <Text style={styles.categoriesText}>Categories</Text>
        <View style={styles.iconContainer}>
          <View style={styles.circleBackground}>
            <TouchableOpacity
              style={styles.icon}
              onPress={() => navigation.navigate('MedicineLists')}
            >
              <Image source={require('../assets/medicine.png')} style={styles.iconImage} />
            </TouchableOpacity>
          </View>
          <View style={styles.circleBackground}>
            <TouchableOpacity
              style={styles.icon}
              onPress={() => navigation.navigate('Vitamins')}
            >
              <Image source={require('../assets/vitamins.png')} style={styles.iconImage} />
            </TouchableOpacity>
          </View>
          <View style={styles.circleBackground}>
            <TouchableOpacity
              style={styles.icon}
              onPress={() => navigation.navigate('MedicineComparer')}
            >
              <Image source={require('../assets/comparing.png')} style={styles.iconImage} />
            </TouchableOpacity>
          </View>
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#C9F5FF',
  },
  upperHalf: {
    height: screenHeight * 0.4,
    flexDirection: 'column',
    alignItems: 'center',
    backgroundColor: '#6499E9',
    justifyContent: 'center',
    borderBottomRightRadius: 40,
    borderBottomLeftRadius: 40,
  },
  lowerHalf: {
    flex: 1,
    paddingHorizontal: 20,
  },
  bigText: {
    fontSize: 40,
    marginBottom: 10,
    color: 'white',
  },
  searchContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: 'white',
    borderRadius: 25,
    paddingLeft: 4,
  },
  searchInput: {
    flex: 10,
    fontSize: 16,
    paddingVertical: 5,
  },
  searchIcon: {
    padding: 10,
  },
  searchImage: {
    width: 20,
    height: 20,
  },
  categoriesText: {
    fontSize: 18,
    fontWeight: 'bold',
  },
  iconContainer: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    marginTop: 20,
  },
  circleBackground: {
    backgroundColor: 'white',
    borderRadius: 50,
    padding: 5,
  },
  icon: {
    alignItems: 'center',
  },
  iconImage: {
    width: 60,
    height: 60,
    resizeMode: 'contain',
  },
});

export default HomePage;
