import React, { useState } from 'react';
import { View, Text, Pressable, StyleSheet, Image, Dimensions, Modal, TouchableOpacity } from 'react-native';

const { height: screenHeight } = Dimensions.get('window');

const Vitamins = () => {
  const [selectedBubble, setSelectedBubble] = useState(null);
  const [modalVisible, setModalVisible] = useState(false);

  const bubbleData = [
    {
      title: 'A',
      description: 'Vitamin A is essential for maintaining good vision and supporting the immune system.',
      goodSource: [
        'Pork\n',
        'Soy\n',
        'Watermelon\n',
      ],
      image: require('../assets/A.png'),
    },
    { title: 'B1', description: 'Description for B1', image: require('../assets/B1.png') },
    { title: 'B2', description: 'Description for B2', image: require('../assets/B2.png') },
    { title: 'B3', description: 'Description for B3', image: require('../assets/B3.png') },
    { title: 'B6', description: 'Description for B6', image: require('../assets/B6.png') },
    { title: 'B7', description: 'Description for B7', image: require('../assets/B7.png') },
    { title: 'B9', description: 'Description for B9', image: require('../assets/B9.png') },
    { title: 'B12', description: 'Description for B12', image: require('../assets/B12.png') },
    { title: 'C', description: 'Description for C', image: require('../assets/C.png') },
    { title: 'D', description: 'Description for D', image: require('../assets/D.png') },
    { title: 'E', description: 'Description for E', image: require('../assets/E.png') },
    { title: 'K', description: 'Description for K', image: require('../assets/K.png') },
  ];

  const handleBubbleClick = (index) => {
    setSelectedBubble(index);
    setModalVisible(true);
  };

  const closeModal = () => {
    setModalVisible(false);
  };

  return (
    <View style={styles.container}>
      <View style={styles.upperHalf}>
        <Image source={require('../assets/vitamins.png')} style={styles.vitaminsImage} />
        <Text style={styles.title}>Vitamins Cheat Sheet</Text>
      </View>
      <View style={styles.lowerHalf}>
        {bubbleData.map((bubble, index) => (
          <Pressable
            key={index}
            onPress={() => handleBubbleClick(index)}
            style={[
              styles.bubbleContainer,
              { backgroundColor: selectedBubble === index ? '#89fff8' : 'white' },
            ]}
          >
            <Text style={styles.bubbleTitle}>{bubble.title}</Text>
          </Pressable>
        ))}
      </View>
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={closeModal}
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalContent}>
            <Text style={styles.modalTitle}>{bubbleData[selectedBubble]?.title}</Text>
            <View style={styles.modalDescriptionContainer}>
              <Text style={styles.modalDescription}>
                <Text style={{ fontWeight: 'bold', fontStyle: 'italic' }}>What we use it for?</Text>
                {'\n'}
                {bubbleData[selectedBubble]?.description}
              </Text>
              <Image source={require('../assets/human2.png')} style={styles.human2Image} />
            </View>
            <Text style={styles.modalGoodSource}>
              <Text style={{ fontWeight: 'bold', fontStyle: 'italic' }}>Good source:</Text>
              {'\n'}
              {bubbleData[selectedBubble]?.goodSource.map((source, i) => (
                <Text key={i}>• {source}</Text>
              ))}
            </Text>
            <TouchableOpacity onPress={closeModal}>
              <Text style={styles.modalCloseButton}>Close</Text>
            </TouchableOpacity>
          </View>
        </View>
      </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FFDF8E',
  },
  upperHalf: {
    height: screenHeight * 0.3,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#f3ff6b',
    borderBottomLeftRadius: 40,
    borderBottomRightRadius: 40,
    position: 'relative',
  },
  vitaminsImage: {
    width: 80,
    height: 80,
    resizeMode: 'cover',
  },
  title: {
    marginTop: 10,
    fontSize: 24,
    fontWeight: 'bold',
    color: '#FF5959',
  },
  lowerHalf: {
    marginTop: 10,
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'space-between',
    paddingHorizontal: 10,
  },
  bubbleContainer: {
    width: '20%',
    aspectRatio: 1,
    borderRadius: 100,
    marginVertical: 5,
    marginLeft: '5%',
    marginRight: '5%',
    justifyContent: 'center',
    alignItems: 'center',
  },
  bubbleTitle: {
    fontSize: 30,
    fontWeight: 'bold',
    color: '#FF5959',
  },
  modalContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'rgba(0,0,0,0.5)',
  },
  modalContent: {
    backgroundColor: 'white',
    padding: 20,
    borderRadius: 10,
    width: '80%',
    
  },
  modalTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#FF5959',
    marginBottom: 10,
  },
  modalDescriptionContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  modalDescription: {
    fontSize: 15,
    marginBottom: 10,
    flex: 1,
  },
  human2Image: {
    width: 300,
    height: 300,
    marginRight: 'auto',
    position: 'relative'
  },
  modalGoodSource: {
    fontSize: 15,
    marginBottom: 10,
  },
  modalCloseButton: {
    fontSize: 20,
    color: '#FF5959',
    textAlign: 'center',
    padding: 10,
    backgroundColor: '#89fff8',
    borderRadius: 10,
  },
});

export default Vitamins;
