import React, { useContext } from "react";
import { View, Text, StyleSheet, Image, TouchableOpacity } from "react-native";
import { NavigationContainer, useNavigation } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";
import LoginScreen from "./Tabs/LoginScreen";
import Button from "./button/button";
import HomePage from "./Tabs/Homepage";
import SignUpScreen from "./Tabs/SignUpScreen";
import MedicineDetails from "./Tabs/MedicineDetails";
import Vitamins from "./Tabs/Vitamins";
import MedicineLists from "./Tabs/Medicinelists";
import MedicineInteractionChecker from "./Tabs/MedicineInteractionChecker";
import ForgotPassword from "./Tabs/ForgotPassword";
import { AuthProvider } from "./services/Auth";

const Stack = createStackNavigator();

const App = () => {
  return (
    <AuthProvider>
      <NavigationContainer>
        <Stack.Navigator initialRouteName="HomeScreen">
          <Stack.Screen name="HomeScreen" component={HomeScreen} />
          <Stack.Screen name="Login" component={LoginScreen} />
          <Stack.Screen
            name="HomePage"
            component={HomePage}
            options={{ headerLeft: null, headerRight: () => <LogoutButton /> }}
          />
          {/* Other screens */}
        </Stack.Navigator>
      </NavigationContainer>
    </AuthProvider>
  );
};

const LogoutButton = () => {
  const { signOut } = useContext(AuthContext);
  const navigation = useNavigation();

  const handleLogout = () => {
    // Perform logout actions
    signOut(); // Assuming a signOut function is available in your AuthContext
    navigation.navigate('HomeScreen'); // Navigate back to the HomeScreen or another desired screen after logout
  };

  return (
    <TouchableOpacity onPress={handleLogout} style={styles.logoutButton}>
      <Text style={styles.logoutText}>Logout</Text>
    </TouchableOpacity>
  );
};

const HomeScreen = ({ navigation }) => {
  return (
    <View style={styles.container}>
      {/* Your existing HomeScreen code */}
    </View>
  );
};


const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    padding: 20,
    backgroundColor: "skyblue",
  },
  header: {
    justifyContent: "center",
    alignItems: "center",
  },
  subheader: {},
  middle: {
    justifyContent: "center",
    alignItems: "center",
  },
  footer: {
    justifyContent: "flex-end",
    alignItems: "center",
  },
  title: {
    fontSize: 36,
    fontWeight: "bold",
  },
  subtitle: {
    fontSize: 15,
    marginBottom: 20,
  },
  logo: {
    width: 300,
    height: 300,
    resizeMode: "contain",
  },
});

export default App;
