import React, { useState } from 'react';
import { View, Picker, Text } from 'react-native';

const DropdownWithDescription = () => {
  const [selectedValue, setSelectedValue] = useState('java');
  const [description, setDescription] = useState('');

  const medicineDescriptions = {
    java: 'This is a description for Java medicine.',
    js: 'This is a description for JavaScript medicine.',
    python: 'This is a description for Python medicine.',
    csharp: 'This is a description for C# medicine.',
  };

  const handleValueChange = (itemValue) => {
    setSelectedValue(itemValue);
    setDescription(medicineDescriptions[itemValue]);
  };

  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Picker
        selectedValue={selectedValue}
        style={{ height: 50, width: 200 }}
        onValueChange={(itemValue) => handleValueChange(itemValue)}
      >
        <Picker.Item label="Java" value="java" />
        <Picker.Item label="JavaScript" value="js" />
        <Picker.Item label="Python" value="python" />
        <Picker.Item label="C#" value="csharp" />
      </Picker>
      <Text style={{ marginTop: 20 }}>{description}</Text>
    </View>
  );
};

export default DropdownWithDescription;
