import React, { useState, useEffect } from 'react';
import { View, Text, Image, Pressable, StyleSheet, Dimensions } from 'react-native';
import ApiService from '../services/ApiService';

const { height: screenHeight } = Dimensions.get('window');

const MedicineDetails = ({ route }) => {
  const { medicineId } = route.params;
  const [medicineDetails, setMedicineDetails] = useState(null);

  useEffect(() => {
    fetchMedicineDetails();
  }, []);

  const fetchMedicineDetails = async () => {
    try {
      const data = await ApiService.getMedicineById(medicineId);
      setMedicineDetails(data);
    } catch (error) {
      console.error(error);
      // Handle error
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.upperHalf}>
        <View style={styles.leftContent}>
          <Image source={require('../assets/human.png')} style={styles.humanImage} />
        </View>
        <View style={styles.rightContent}>
          <Image source={medicineDetails?.image} style={styles.medicineImage} />
          <Text style={styles.medicineName}>{medicineDetails?.name}</Text>
        </View>
      </View>
      <View style={styles.lowerHalf}>
        {medicineDetails && medicineDetails.categories && (
          <View>
            {medicineDetails.categories.map((category, index) => (
              <Pressable key={index} style={styles.barContainer}>
                <View style={styles.barTitleContainer}>
                  <Text style={styles.barTitle}>{category.title}</Text>
                  <Text style={styles.downArrow}>â–¼</Text>
                </View>
                <View style={styles.infoContainer}>
                  <Text style={styles.infoText}>{category.info}</Text>
                </View>
              </Pressable>
            ))}
          </View>
        )}
        <Text style={styles.consultDoctorText}>
          If symptoms persist, consult your doctor.
        </Text>
      </View>
    </View>
  );
};


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#FADEFF",
  },
  upperHalf: {
    height: screenHeight * 0.3,
    justifyContent: "center",
    backgroundColor: "#F2ABFF",
    alignItems: "center",
    borderBottomLeftRadius: 40,
    borderBottomRightRadius: 40,
  },
  logoBackground: {
    width: 100,
    height: 100,
    backgroundColor: "white",
    borderRadius: 50,
    alignItems: "center",
    justifyContent: "center",
  },
  logo: {
    width: 80,
    height: 80,
    resizeMode: "cover",
  },
  lowerHalf: {
    paddingHorizontal: 20,
    marginTop: 10,
  },
  title: {
    fontSize: 30,
    fontWeight: "bold",
    marginBottom: 10,
    color: "#A3007F",
  },
  barContainer: {
    marginBottom: 10,
    borderColor: "#ccc",
    padding: 6,
    borderRadius: 20,
    backgroundColor: "white",
    fontStyle: "italic",
  },
  barTitleContainer: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "baseline",
  },
  barTitle: {
    fontSize: 16,
    fontWeight: "italic",
    marginBottom: 5,
  },
  downArrow: {
    fontSize: 12,
  },
  medicineList: {
    marginVertical: 10,
  },
  medicineItem: {
    borderColor: "#ccc",
    padding: 6,
    borderRadius: 20,
    backgroundColor: "white",
    marginVertical: 5,
  },
  medicineText: {
    fontSize: 16,
  },
});

export default MedicineLists;
