import React, { useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Image, Dimensions } from 'react-native';

const { height: screenHeight } = Dimensions.get('window');

const MedicineDetails = () => {
  const [selectedCategory, setSelectedCategory] = useState(null);

  const categories = [
    {
      title: 'Description',
      info: 'This is the description of the medicine.',
    },
    {
      title: 'Dosage and Frequency',
      info: 'Dosage and frequency information for the medicine.',
    },
    {
      title: 'Precautions',
      info: 'Precautionary measures for taking the medicine.',
    },
    {
      title: 'Overdosage of the medicine.',
      info: 'Information about overdosage of the medicine. \n -eating disorder',
    },
  ];

  const handleCategoryClick = (index) => {
    setSelectedCategory(index === selectedCategory ? null : index);
  };

  const categoryStyles = [
    { backgroundColor: '#E1F5FE' }, // Style for Description category
    { backgroundColor: '#E8F5E9' }, // Style for Dosage and Frequency category
    { backgroundColor: '#FFF9C4' }, // Style for Precautions category
    { backgroundColor: '#FFEBEE' }, // Style for Overdosage category
  ];

  return (
    <View style={styles.container}>
      <View style={styles.upperHalf}>
        <View style={styles.leftContent}>
          <Image source={require('C:/Users/makir/OneDrive/Desktop/Makiramdam_026_41S6/Project/Project/assets/human.png')} style={styles.humanImage} />
        </View>
        <View style={styles.rightContent}>
          <Image source={require('C:/Users/makir/OneDrive/Desktop/Makiramdam_026_41S6/Project/Project/assets/biogesic.jpg')} style={styles.medicineImage} />
          <Text style={styles.medicineName}>Medicine Name</Text>
        </View>
        <TouchableOpacity style={styles.backIconContainer}>
          <View style={styles.backIconCircle}>
            <Image source={require('C:/Users/makir/OneDrive/Desktop/Makiramdam_026_41S6/Project/Project/assets/backicon.png')} style={styles.backIcon} />
          </View>
        </TouchableOpacity>
      </View>
      <View style={styles.lowerHalf}>
        {categories.map((category, index) => (
          <TouchableOpacity
            key={index}
            onPress={() => handleCategoryClick(index)}
            style={[styles.barContainer, categoryStyles[index]]}
          >
            <View style={styles.barTitleContainer}>
              <Text style={styles.barTitle}>{category.title}</Text>
              <Text style={styles.downArrow}>â–¼</Text>
            </View>
            {selectedCategory === index && (
              <View style={styles.infoContainer}>
                <Text style={styles.infoText}>{category.info}</Text>
              </View>
            )}
          </TouchableOpacity>
        ))}
        <Text style={styles.consultDoctorText}>
          If symptoms persist, consult your doctor.
        </Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#00F1C6',
    paddingTop: 20,
    paddingHorizontal: 20,
  },
  upperHalf: {
    height: screenHeight * 0.4,
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#89fff8',
    borderRadius: 20,
    padding: 20,
    marginBottom: 10,
    borderWidth: 0,
  },
  leftContent: {
    flex: 1,
    alignItems: 'flex-start',
  },
  rightContent: {
    flex: 1,
    alignItems: 'flex-end',
    justifyContent: 'center',
  },
  humanImage: {
    flex: 1,
    width: 170,
    height: 170,
    resizeMode: 'cover',
  },
  medicineImage: {
    width: 80,
    height: 80,
    resizeMode: 'cover',
  },
  medicineName: {
    marginTop: 5,
    fontSize: 12,
    fontWeight: 'bold',
  },
  lowerHalf: {
    marginTop: 10,
    flex: 1,
  },
  barContainer: {
    marginBottom: 10,
    borderColor: '#ccc',
    padding: 6,
    borderRadius: 20,
    backgroundColor: 'white',
    fontStyle: 'italic',
  },
  barTitleContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'baseline',
  },
  barTitle: {
    fontSize: 16,
    fontWeight: 'italic',
    marginBottom: 5,
  },
  downArrow: {
    fontSize: 12,
  },
  infoContainer: {
    marginTop: 10,
  },
  infoText: {
    fontSize: 14,
  },
  backIconContainer: {
    position: 'absolute',
    top: 20,
    left: 20,
  },
  backIconCircle: {
    width: 40,
    height: 40,
    borderRadius: 20,
    backgroundColor: 'white',
    justifyContent: 'center',
    alignItems: 'center',
  },
  backIcon: {
    width: 20,
    height: 20,
  },
  consultDoctorText: {
    marginTop: 10,
    textAlign: 'center',
    fontSize: 14,
    fontStyle: 'italic',
    color: '#555',
  },
});

export default MedicineDetails;
