import React, { useState, useEffect } from "react";
import {
  View,
  Text,
  Image,
  StyleSheet,
  Dimensions,
  Button,
} from "react-native";
import DropDownPicker from "react-native-dropdown-picker";
import ApiService from "../services/ApiService";

const { height: screenHeight } = Dimensions.get("window");

const MedicineInteractionChecker = () => {
  const [medicines, setMedicines] = useState([]);
  const [selectedMedicine1, setSelectedMedicine1] = useState(null);
  const [selectedMedicine2, setSelectedMedicine2] = useState(null);
  const [interactionResult, setInteractionResult] = useState('');

  useEffect(() => {
    fetchMedicines();
  }, []);

  const fetchMedicines = async () => {
    try {
      const medicineList = await ApiService.getAllMedicine();
      const translatedMedicineList = medicineList.map((item) => ({
        label: item.name,
        value: item.id,
      }));
      setMedicines(translatedMedicineList);
    } catch (error) {
      console.error(error);
      // Handle error
    }
  };

  const handleCheckInteraction = async () => {
    if (selectedMedicine1 && selectedMedicine2) {
      try {
        const result = await ApiService.checkMedicineInteraction(
          selectedMedicine1.value,
          selectedMedicine2.value
        );
        console.log("Interaction Result:", result);
        setInteractionResult(result ?? "No interaction found.");
        // Handle the interaction result as needed
      } catch (error) {
        console.error(error);
        // Handle error
      }
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.upperHalf}>
        <View style={styles.logoBackground}>
          <Image
            source={require("../assets/comparing.png")}
            style={styles.logo}
          />
        </View>
        <Text style={styles.title}>Medicine Interaction Checker</Text>
      </View>
      <View style={styles.lowerHalf}>
        <DropDownPicker
          items={medicines}
          placeholder="Select Medicine 1"
          containerStyle={styles.dropdownContainer}
          style={styles.dropdown}
          onChangeItem={(item) => setSelectedMedicine1(item)}
        />
        <DropDownPicker
          items={medicines}
          placeholder="Select Medicine 2"
          containerStyle={styles.dropdownContainer}
          style={styles.dropdown}
          onChangeItem={(item) => setSelectedMedicine2(item)}
        />
        <Button title="Check Interaction" onPress={handleCheckInteraction} />
        <Text style={styles.interactionText}>
          Interaction: {interactionResult}
        </Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#FFA0D2",
  },
  upperHalf: {
    height: screenHeight * 0.3,
    justifyContent: "center",
    backgroundColor: "#FFC8E5",
    alignItems: "center",
    borderBottomLeftRadius: 40,
    borderBottomRightRadius: 40,
  },
  logoBackground: {
    width: 100,
    height: 100,
    backgroundColor: "white",
    borderRadius: 50,
    alignItems: "center",
    justifyContent: "center",
  },
  logo: {
    width: 80,
    height: 80,
    resizeMode: "cover",
  },
  lowerHalf: {
    paddingHorizontal: 20,
    paddingTop: 20,
  },
  title: {
    fontSize: 25,
    fontWeight: "bold",
    marginBottom: 10,
    color: "#FFA0D2",
  },
  dropdownContainer: {
    marginTop: 10,
  },
  dropdownItem: {
    padding: 10,
    marginTop: 2,
    backgroundColor: "#FAF9F8",
    borderColor: "#ddd",
    borderBottomWidth: 1,
  },
  dropdownItemText: {
    fontSize: 18,
    color: "#000",
  },
  searchInput: {
    padding: 10,
    borderWidth: 1,
    borderColor: "#ccc",
    borderRadius: 5,
    backgroundColor: "#fff",
  },
});

export default MedicineInteractionChecker;
