import React, { useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Image, Dimensions } from 'react-native';

const { height: screenHeight } = Dimensions.get('window');

const MedicineDetails = () => {
  const [selectedCategory, setSelectedCategory] = useState(null);

  const categories = [
    {
      title: 'Description',
      info: 'This is the description of the medicine.',
    },
    {
      title: 'Dosage and Frequency',
      info: 'Dosage and frequency information for the medicine.',
    },
    {
      title: 'Precautions',
      info: 'Precautionary measures for taking the medicine.',
    },
    {
      title: 'Overdosage of the medicine.',
      info: 'Information about overdosage of the medicine.',
    },
  ];

  const handleCategoryClick = (index) => {
    setSelectedCategory(index === selectedCategory ? null : index);
  };

  return (
    <View style={styles.container}>
      <View style={styles.upperHalf}>
        <View style={styles.leftContent}>
          <Image source={require('C:/Users/makir/OneDrive/Desktop/Makiramdam_026_41S6/Project/Project/assets/human.png')} style={styles.humanImage} />
        </View>
        <View style={styles.rightContent}>
          <Image source={require('C:/Users/makir/OneDrive/Desktop/Makiramdam_026_41S6/Project/Project/assets/biogesic.jpg')} style={styles.medicineImage} />
          <Text style={styles.medicineName}>Medicine Name</Text>
        </View>
      </View>
      <View style={styles.lowerHalf}>
        {categories.map((category, index) => (
          <TouchableOpacity
            key={index}
            onPress={() => handleCategoryClick(index)}
            style={styles.barContainer}
          >
            <View style={styles.barTitleContainer}>
              <Text style={styles.barTitle}>{category.title}</Text>
              <Text style={styles.downArrow}>â–¼</Text>
            </View>
            {selectedCategory === index && (
              <View style={styles.infoContainer}>
                <Text style={styles.infoText}>{category.info}</Text>
              </View>
            )}
          </TouchableOpacity>
        ))}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#00F1C6',
    paddingTop: 20,
    paddingHorizontal: 20,
  },
  upperHalf: {
    height: screenHeight * 0.4, // Adjust the height to match the lower half upper part

    alignItems: 'stretch',
    backgroundColor: '#89fff8',



  },
  leftContent: {
    flex: 1,
    alignSelf: 'flex-start'
  },
  rightContent: {
    flex: 1,
    alignItems: 'flex-end',
  },
  humanImage: {
    width: 150,
    height: 150,
    //resizeMode: 'cover',
    alignItems: 'flex-end'
  },
  medicineImage: {
    width: 80,
    height: 80,
    resizeMode: 'cover',
  },
  medicineName: {
    marginTop: 5,
    fontSize: 16,
    fontWeight: 'bold',
  },
  lowerHalf: {
    marginTop: 10, // Slight margin from the top
  },
  barContainer: {
    marginBottom: 10,
    borderWidth: 1,
    borderColor: '#ccc',
    padding: 5,
    borderRadius: 15,
    width: '100%',
    backgroundColor: 'white',
  },
  barTitleContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'baseline',
  },
  barTitle: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 2,
  },
  downArrow: {
    fontSize: 12,
  },
  infoText: {
    fontSize: 14,
  },
});

export default MedicineDetails;
