import React, { useState, useEffect } from "react";
import {
  View,
  Text,
  Image,
  TextInput,
  TouchableOpacity,
  StyleSheet,
  Dimensions,
  Alert,
} from "react-native";
import { useNavigation, useRoute } from "@react-navigation/native";
import ApiService from "../services/ApiService"; // Import ApiService

const { height: screenHeight } = Dimensions.get("window");

const HomePage = () => {
  const navigation = useNavigation();
  const route = useRoute();

  const [fullName, setFullName] = useState('');

  useEffect(() => {
    fetchUserData(); // Fetch user data on component mount
  }, []);

  const fetchUserData = async () => {
    try {
      const userData = await ApiService.getUserDetail(); // Fetch user details
      setFullName(userData.fullName); // Update the state with user's full name
    } catch (error) {
      console.error('Error fetching user data:', error.message);
      // Handle error while fetching user data
    }
  };
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#C9F5FF",
  },
  upperHalf: {
    height: screenHeight * 0.4,
    flexDirection: "column",
    alignItems: "center",
    backgroundColor: "#6499E9",
    justifyContent: "center",
    borderBottomRightRadius: 40,
    borderBottomLeftRadius: 40,
  },
  lowerHalf: {
    flex: 1,
    paddingHorizontal: 20,
  },
  smallText: {
    fontSize: 12,
    marginBottom: 10,
    position: "relative",
    marginLeft: -200,
    color: "white",
  },
  bigText: {
    fontSize: 40,
    marginBottom: 10,
    position: "relative",
    color: "white",
  },
  searchContainer: {
    flexDirection: "row",
    alignItems: "center",
    backgroundColor: "white",
    borderRadius: 25,
    paddingHorizontal: 20, // Adjusted padding
    marginHorizontal: 10, // Adjusted margin
    marginTop: 10, // Adjusted margin
  },
  searchInput: {
    flex: 1, // Adjusted flex
    fontSize: 16,
    paddingVertical: 5,
  },
  searchIcon: {
    padding: 10,
    marginLeft: "auto", // Align to the right end
  },
  searchImage: {
    width: 20,
    height: 20,
  },
  calendarContainer: {
    marginTop: 20,
    aspectRatio: 1,
    overflow: "scroll",
    borderRadius: 20,
    borderBottomLeftRadius: 40,
    borderBottomRightRadius: 40,
  },
  categoriesText: {
    fontSize: 18,
    fontWeight: "bold",
  },
  iconContainer: {
    flexDirection: "row",
    justifyContent: "space-around",
    marginTop: 20,
  },
  circleBackground: {
    backgroundColor: "white",
    borderRadius: 50,
    padding: 5,
  },
  icon: {
    alignItems: "center",
  },
  iconImage: {
    width: 60,
    height: 60,
    resizeMode: "contain",
  },
});

export default HomePage;
